import React, { Component } from "react";
import "./index.css";

export default class Reminder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      color1: "#00FA92",
      color2: "#e61819",
      list: [
        // {
        //   title: "a",
        //   checked: true,
        // },
        // {
        //   title: "b",
        //   checked: false,
        // },
        // {
        //   title: "c",
        //   checked: false,
        // },
      ],
    };
  }

  addData = (e) => {
    let title = this.title.value;
    let tempList = this.state.list;

    if (this.title.value == "") {
      alert("please insert data");
    }

    if (this.title.value != "") {
      tempList.push({
        title: title,
        checked: false,
      });

      this.setState({
        list: tempList,
      });

      this.title.value = "";

      localStorage.setItem("list", JSON.stringify(tempList));
    }
  };

  changeCheck = (key) => {
    let tempList = this.state.list;
    tempList[key].checked = !tempList[key].checked;
    this.setState({
      list: tempList,
    });
    localStorage.setItem("list", JSON.stringify(tempList));
  };

  removeData = (key) => {
    let tempList = this.state.list;
    tempList.splice(key, 1);
    this.setState({
      list: tempList,
    });
    localStorage.setItem("list", JSON.stringify(tempList));
  };

  componentDidMount() {
    var prelist = JSON.parse(localStorage.getItem("list"));
    var setColor1 = JSON.parse(localStorage.getItem("setColor1"));
    var setcolor2 = JSON.parse(localStorage.getItem("setColor2"));
    if (prelist) {
      this.setState({
        list: prelist,
      });
    }
    this.setState({color1: setColor1});
    this.setState({color2: setcolor2});
  }

  changeColor = (e) => {
      var color1 = this.state.color1;
      var color2 = this.state.color2;
    if (this.name1) {
      this.setState({ color1: this.name1.value });
    }
    if (this.name2) {
      this.setState({ color2: this.name2.value });
    }
    localStorage.setItem("setColor1", JSON.stringify(color1))
    localStorage.setItem("setColor2",JSON.stringify(color2))
  };


  reset = () => {
    this.setState({ color1: "#00FA92",});
    this.setState({ color2: "#e61819" });
  };

  render() {
    return (
      <div
        className="container"
        style={{
          background: `linear-gradient( 60deg, ${this.state.color1}, ${this.state.color2}`,
        }}
      >
        <header>
          <div className="title">Reminder </div>
          <input ref={(ref) => (this.title = ref)} />
          <button className="add" onClick={this.addData}>
            +
          </button>
        </header>

        <div className="items">
          <h2 className="todo">Items To Do:</h2>
          <hr />
          <ul class="ul1">
            {this.state.list.map((value, key) => {

              if (!value.checked) {
                return (
                  <li>
                    <input
                      type="checkbox"
                      checked={value.checked}
                      onChange={this.changeCheck.bind(this, key)}
                    />{" "}
                    {value.title}{" "}
                    <button
                      className="delete"
                      onClick={this.removeData.bind(this, key)}
                    >
                      {" "}
                      X{" "}
                    </button>
                  </li>
                );
              }
            })}
          </ul>
        </div>

        <div className="items">
          <h2 className="done">Items Done:</h2>
          <br/><hr /><br/>
          <ul className="ul2">
            {this.state.list.map((value, key) => {
              if (value.checked) {
                return (
                  <li>
                    <input
                      type="checkbox"
                      checked={value.checked}
                      onChange={this.changeCheck.bind(this, key)}
                    />{" "}
                    {value.title}{" "}
                    <button
                      className="delete"
                      onClick={this.removeData.bind(this, key)}
                    >
                      X
                    </button>
                  </li>
                );
              }
            })}
          </ul>
        </div>

        <footer>
          <div className="inputGroup">
            <label>Please choose the first color </label>
            <input
              type="color"
              value={this.state.color1}
              ref={(ref) => (this.name1 = ref)}
              name="color1"
              className="inputColor"
              onChange={this.changeColor}
            />
          </div>
          <div className="inputGroup">
            <label>Please choose the second color </label>
            <input
              type="color"
              value={this.state.color2}
              ref={(ref) => (this.name2 = ref)}
              name="color2"
              className="inputColor"
              onChange={this.changeColor}
            />
          </div>
          <div className="inputGroup">
            <button className="reset" onClick={this.reset}>
              reset background
            </button>
          </div>
        </footer>
      </div>
    );
  }
}
